//Linha Focuss 1.1
(function($){$.fn.focuss=function(options){return new $.focuss(options,this);};$.focuss=function(options,elem){var padrao={seletor:'input, textarea',evento:'focus',eventoFim:'blur',tempoIn:'normal',tempoOut:'normal',classe:'focus',removeTexto:false,onInicia:null,onTermina:null,live:false,liveTempo:100};var o=$.extend(padrao,options),$d=$(document);if(elem===undefined){elem=$(o.seletor);}
elem.elements=[];$d.delegate(elem.selector,'iniciaFocuss',function(){var elems=elem.elements,els=$(elem.selector,elem.context),nEls=els.not(elems).not(':submit');elem.elements=els;nEls.each(function(){var $t=$(this),texto=$(this).val();$t.bind(o.evento,function(){return animaFocuss($t,texto);});$t.bind(o.eventoFim,function(){return terminaFocuss($t,texto);});});});if(o.live){setInterval(function(){$(elem.selector).trigger('iniciaFocuss');},o.liveTempo);}else{if(elem.length){elem.trigger('iniciaFocuss');}}
function animaFocuss($t,texto){if(o.removeTexto){if($t.val()==texto){$t.val('');}}
$t.addClass(o.classe);if($.isFunction(o.onInicia)){o.onInicia.apply($t,new Array($t,texto,o));}}
function terminaFocuss($t,texto){if(o.removeTexto){if($t.val()==''){$t.val(texto);}}
$t.removeClass(o.classe);if($.isFunction(o.onTermina)){o.onTermina.apply($t,new Array($t,texto,o));}}};})(jQuery);
